# função para recomendar destinos de viagem
# categoria: natureza, cultura, aventura e vidanoturna
# hospedagem: hotel, airbnb, hostel e acampamento
# clima: equatorial, subtropical e tropical
# alimentacao: massa, churrasco, hamburger, sushi e sobremesas
def recommend_data(
    categoria: str, hospedagem: str, clima: str, alimentacao: str
) -> dict[str, str] | None:
    print(f"Categoria: {categoria}.\n Hospedagem: {hospedagem}.\n Clima: {clima}.\nAlimentacao: {alimentacao}")
    environment.reset()
    template = environment.find_template('preferencia')

    template.assert_fact(
        categoria=clips.Symbol(categoria),
        hospedagem=clips.Symbol(hospedagem),
        clima=clips.Symbol(clima),
        alimentacao=clips.Symbol(alimentacao)
    )
    environment.run()

    recommendation_facts = [
        fact for fact in environment.facts() if fact.template.name == "recommendation"
    ]

    for recommendation in recommendation_facts:
        return {
            "cidade": recommendation["cidade"],
            "ponto": recommendation["ponto"],
            "descricao_ponto": recommendation["descricao_ponto"],
            "descricao_alimentacao": recommendation["descricao_alimentacao"],
            "descricao_hospedagem": recommendation["descricao_hospedagem"],
        }