import clips

preference = """
(deftemplate preferencia
    (slot ponto (type SYMBOL))
    (slot hospedagem (type SYMBOL))
    (slot clima (type SYMBOL))
    (slot alimentacao (type SYMBOL)))
"""
city = """
(deftemplate recommendation
    (slot cidade))
"""

environment = clips.Environment()
environment.build(preference)
environment.build(city)

r1 = '(defrule r1 (preferencia (ponto natureza) (hospedagem hotel) (clima equatorial) (alimentacao massa)) => (assert (recommendation (cidade "Salvador"))))'; environment.build(r1)
