def recommend_data(ponto, hospedagem, clima, alimentacao):
    template = environment.find_template('preferencia')
    fact = template.assert_fact(ponto=clips.Symbol(ponto),hospedagem=clips.Symbol(hospedagem),clima=clips.Symbol(clima),alimentacao=clips.Symbol(alimentacao))
    environment.run()
    recommendation_facts = [fact for fact in environment.facts() if fact.template.name == "recommendation"]
    for recommendation in recommendation_facts:
        return recommendation["cidade"]

